$(document).ready(function () {
  $(".card-deck").slick({
    infinite: !1,
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: !1,
    dots: !0,
    cssEase: "linear",
    responsive: [
      { breakpoint: 1920, settings: { slidesToShow: 1 } },
      { breakpoint: 992, settings: { slidesToShow: 1, dots: !0 } },
      { breakpoint: 575, settings: { slidesToShow: 1, dots: !0 } },
    ],
  });
});
